import { Badge } from "@/components/ui/badge"
import { workContent as defaultContent } from "@/lib/constants/pages/experience"
import { client } from "@/sanity/lib/client"
import { experienceQuery } from "@/sanity/lib/queries"

export const revalidate = 60;

export default async function ExperiencePage() {
  const sanityExperience = await client.fetch(experienceQuery).catch(() => []);

  // Transform Sanity data or use default
  const experiences = sanityExperience.length > 0 ? sanityExperience.map((exp: any) => ({
    title: exp.title,
    company: exp.company,
    duration: exp.duration,
    location: exp.location,
    description: exp.description,
    technologies: exp.technologies || []
  })) : defaultContent.experience;

  const title = defaultContent.title;
  const description = defaultContent.description;

  return (
    <div className="min-h-screen py-20 px-6 bg-white">
      <div className="container mx-auto max-w-5xl">
        <div className="text-center mb-20">
          <h1 className="text-5xl md:text-7xl font-black text-gray-900 mb-6 tracking-tight">
            {title}
          </h1>
          <p className="text-lg text-gray-600 max-w-2xl mx-auto font-light">
            {description}
          </p>
        </div>

        <div className="space-y-8">
          {experiences.map((exp: any, i: number) => (
            <div key={i} className="bg-gray-50 border border-gray-200 rounded-xl p-8">
              <div className="flex justify-between items-start mb-4">
                <div>
                  <h3 className="text-2xl font-bold text-gray-900">{exp.title}</h3>
                  <p className="text-lg font-semibold text-gray-700">{exp.company}</p>
                </div>
                <div className="text-right">
                  <p className="text-sm text-gray-600">{exp.duration}</p>
                  <p className="text-sm text-gray-600">{exp.location}</p>
                </div>
              </div>
              <p className="text-gray-700 mb-6 leading-relaxed">
                {exp.description}
              </p>
              <div className="flex flex-wrap gap-2">
                {exp.technologies.map((tech: string, j: number) => (
                  <Badge key={j} variant="outline">{tech}</Badge>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  )
}

import { Badge } from "@/components/ui/badge"
import { Button } from "@/components/ui/button"
import { ExternalLink, Github } from "lucide-react"
import Link from 'next/link'
import { workContent as defaultContent } from "@/lib/constants/pages/work"
import { client } from "@/sanity/lib/client"
import { workQuery } from "@/sanity/lib/queries"

export const revalidate = 60;

export default async function WorkPage() {
  const sanityProjects = await client.fetch(workQuery).catch(() => []);

  // Transform Sanity data to match component structure
  const projects = sanityProjects.length > 0 ? sanityProjects.map((p: any) => ({
    title: p.title,
    category: p.category,
    description: p.description,
    technologies: p.technologies || [],
    live_link: p.liveLink,
    code_link: p.codeLink,
  })) : defaultContent.projects;

  const title = defaultContent.title; // For now keeping static title
  const description = defaultContent.description;

  return (
    <div className="min-h-screen py-20 px-6 bg-white">
      <div className="container mx-auto max-w-5xl">
        <div className="text-center mb-16">
          <h1 className="text-5xl md:text-7xl font-black text-gray-900 mb-6 tracking-tight">
            {title}
          </h1>
          <p className="text-lg text-gray-600 max-w-2xl mx-auto font-light">
            {description}
          </p>
        </div>

        <div className="grid md:grid-cols-2 gap-8">
          {projects.map((project: any, i: number) => (
            <div key={i} className="bg-gray-50 border border-gray-200 rounded-xl overflow-hidden transform hover:scale-105 transition-transform duration-300">
              <div className="p-6">
                <div className="flex items-center gap-3 mb-4">
                  <Badge className="bg-gray-900 text-white text-xs px-2 py-1">
                    {project.category}
                  </Badge>
                </div>

                <h3 className="text-xl font-bold text-gray-900 mb-3">
                  {project.title}
                </h3>

                <p className="text-gray-700 mb-6 leading-relaxed text-sm">
                  {project.description}
                </p>

                <div className="flex flex-col justify-between items-start gap-4">
                  <div className="flex flex-wrap gap-2">
                    {project.technologies.map((tech: string, j: number) => (
                      <span
                        key={j}
                        className="text-xs px-2 py-1 bg-white text-gray-700 rounded-md font-medium border border-gray-200"
                      >
                        {tech}
                      </span>
                    ))}
                  </div>

                  <div className="flex gap-3 mt-4">
                    {project.live_link && project.live_link !== "#" && (
                      <Link target="_blank" href={project.live_link}>
                        <Button size="sm" className="bg-gray-900 hover:bg-gray-800 text-white text-xs font-medium px-4 py-2">
                          <ExternalLink className="h-3 w-3 mr-2" />
                          View Live
                        </Button>
                      </Link>
                    )}
                    {project.code_link && project.code_link !== "#" && (
                      <Link target="_blank" href={project.code_link}>
                        <Button variant="outline" size="sm" className="text-xs font-medium px-4 py-2 border-gray-300">
                          <Github className="h-3 w-3 mr-2" />
                          Code
                        </Button>
                      </Link>
                    )}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  )
}

"use client";
import { Button } from "@/components/ui/button";
import { ArrowUpLeft, Calendar } from "lucide-react";
import { useState, useRef, useEffect } from "react";
import { contactContent } from "@/lib/constants/pages/contact";

export default function Chat() {
  const chatRef = useRef<HTMLDivElement>(null);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const [isHovered, setIsHovered] = useState(false);
  const [isOpen, setIsOpen] = useState(false);
  const [message, setMessage] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [messages, setMessages] = useState<
    Array<{ text: string; sender: "user" | "bot" }>
  >([]);

  // Helper function to check if message mentions scheduling/call
  const shouldShowCalendlyButton = (text: string): boolean => {
    const keywords = [
      "schedule", "call", "meeting", "calendly", "book", "appointment",
      "discuss", "talk", "consultation", "chat with", "connect"
    ];
    const lowerText = text.toLowerCase();
    return keywords.some(keyword => lowerText.includes(keyword));
  };

  useEffect(() => {
    function handleClickOutside(event: MouseEvent) {
      if (chatRef.current && !chatRef.current.contains(event.target as Node)) {
        setIsOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const handleSend = async () => {
    if (message.trim()) {
      const userMessage = message.trim();
      setMessage("");
      
      // Add user message to the conversation
      const updatedMessages = [...messages, { text: userMessage, sender: "user" as const }];
      setMessages(updatedMessages);
      setIsTyping(true);
      
      try {
        // Send entire conversation history to the backend
        const response = await fetch("/api/chat", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            messages: updatedMessages
          }),
        });
        
        if (!response.ok) {
          throw new Error("Failed to get response");
        }
        
        const data = await response.json();
        setMessages((prev) => [...prev, { text: data.content, sender: "bot" }]);
      } catch (error) {
        console.error("Chat error:", error);
        setMessages((prev) => [...prev, { 
          text: "Sorry, I encountered an error. Please try again.", 
          sender: "bot" 
        }]);
      } finally {
        setIsTyping(false);
      }
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === "Enter") {
      handleSend();
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setMessage(e.target.value);
    setIsTyping(e.target.value.length > 0);
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  return (
    <div className="fixed bottom-4 right-4 sm:bottom-8 sm:right-8 z-50 " ref={chatRef}>
      {/* Chat Dialog */}
      <div
        className={`absolute bottom-0 right-0 transition-all duration-500 ease-out origin-bottom-right
          ${isOpen 
            ? "scale-100 opacity-100 translate-y-0" 
            : "scale-0 opacity-0 translate-y-4 pointer-events-none"
          }`}
      >
        <div className="w-[calc(100vw-32px)] sm:w-[380px] backdrop-blur-lg bg-background/80 rounded-3xl shadow-2xl border border-border overflow-hidden">
          {/* Header with Close Button */}
          <div className="flex items-center justify-between p-4 border-b border-border/50">
            <button
              onClick={() => setIsOpen(false)}
              className="w-8 h-8 rounded-full bg-primary text-primary-foreground 
                        flex items-center justify-center shadow-sm
                        transition-all duration-200 hover:scale-110 hover:shadow-md
                        focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2"
            >
              <ArrowUpLeft className="w-4 h-4 rotate-180" />
            </button>
            <h3 className="text-sm font-medium text-foreground">Chat</h3>
            <div className="w-8 h-8"></div> {/* Spacer for centering */}
          </div>

          {/* Messages Area */}
          <div className="w-full h-[60vh] sm:h-[400px] flex flex-col">
            {messages.length > 0 ? (
              <div className="flex-1 p-4 overflow-y-auto space-y-3 scrollbar-hide">
                {messages.map((msg, index) => (
                  <div
                    key={index}
                    className={`flex flex-col ${msg.sender === "user" ? "items-end" : "items-start"}`}
                  >
                    <div
                      className={`p-3 rounded-2xl text-sm max-w-[80%] sm:max-w-[280px] transition-all duration-300 
                        ${msg.sender === "user"
                          ? "bg-primary text-primary-foreground rounded-br-md"
                          : "bg-muted text-muted-foreground rounded-bl-md"
                        }`}
                    >
                      {msg.text}
                    </div>
                    {/* Show Calendly button for bot messages that mention scheduling */}
                    {msg.sender === "bot" && shouldShowCalendlyButton(msg.text) && (
                      <a
                        href={contactContent.calendly}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="mt-2 inline-flex items-center gap-2 px-4 py-2 
                                  bg-primary text-primary-foreground text-xs font-medium
                                  rounded-xl shadow-sm
                                  hover:shadow-md hover:scale-105
                                  transition-all duration-200
                                  focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2"
                      >
                        <Calendar className="w-3.5 h-3.5" />
                        Schedule a Call
                      </a>
                    )}
                  </div>
                ))}
                <div ref={messagesEndRef} />
              </div>
            ) : (
              <div className="flex-1 flex items-center justify-center p-8">
                <div className="text-center space-y-2">
                  <div className="w-12 h-12 rounded-full bg-muted flex items-center justify-center mx-auto">
                    <ArrowUpLeft className="w-6 h-6 text-muted-foreground" />
                  </div>
                  <p className="text-sm text-muted-foreground">Start a conversation</p>
                </div>
              </div>
            )}

            {/* Input Area */}
            <div className="p-4 border-t border-border/50">
              <div className="relative flex items-center gap-2">
                <input
                  type="text"
                  value={message}
                  onChange={handleChange}
                  onKeyPress={handleKeyPress}
                  placeholder="Type your message..."
                  className="flex-1 p-3 rounded-2xl text-sm 
                            bg-background/60 border border-border
                            transition-all duration-200
                            focus:outline-none focus:ring-2 focus:ring-ring focus:border-transparent
                            placeholder:text-muted-foreground"
                  autoFocus={isOpen}
                />
                <Button
                  onClick={handleSend}
                  disabled={!message.trim()}
                  className="h-10 w-10 p-0 rounded-xl shrink-0"
                >
                  <ArrowUpLeft className="w-4 h-4 rotate-45" />
                </Button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Floating Action Button */}
      <div
        className={`transition-all duration-500 ease-out
          ${isOpen 
            ? "scale-0 opacity-0 pointer-events-none" 
            : "scale-100 opacity-100"
          }`}
      >
        <button
          className={`w-14 h-14 rounded-full bg-primary text-primary-foreground 
                      flex items-center justify-center shadow-lg
                      transition-all duration-300 hover:scale-110 hover:shadow-xl
                      focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2
                      ${isHovered ? "shadow-xl" : ""}`}
          onMouseEnter={() => setIsHovered(true)}
          onMouseLeave={() => setIsHovered(false)}
          onClick={() => setIsOpen(true)}
        >
          <ArrowUpLeft className="w-6 h-6" />
        </button>
      </div>
    </div>
  );
}

'use client'

import { useState, useEffect } from 'react'
import Link from 'next/link'
import { Button } from '@/components/ui/button'

interface HomeContent {
    hero: {
        title: string
        subtitle: string
        description: string
        cta: {
            primary: {
                text: string
                href: string
            }
            secondary: {
                text: string
                href: string
            }
        }
    }
}

export default function HomeClient({ content }: { content: HomeContent }) {
    const [isLoaded, setIsLoaded] = useState(false)

    useEffect(() => {
        setIsLoaded(true)
    }, [])

    return (
        <div className="mt-[-3.5rem] min-h-screen bg-white dark:bg-gray-900">
            <section className="relative min-h-screen flex items-center justify-center px-6 overflow-hidden">
                <div className="absolute inset-0 z-0">
                    <img
                        src="/wordcloud.png"
                        alt="Word Cloud"
                        className="absolute inset-0 w-full h-full object-cover opacity-12 dark:opacity-10"
                    />
                    <div className="absolute inset-0 bg-gradient-to-b from-transparent via-white to-transparent dark:via-gray-900"></div>
                </div>

                <div className={`relative z-10 text-center max-w-4xl mx-auto transition-all duration-1000 ${isLoaded ? 'translate-y-0 opacity-100' : 'translate-y-4 opacity-0'
                    }`}>
                    <h1 className="text-6xl md:text-8xl font-black mb-4 text-gray-900 dark:text-white tracking-tight">
                        {content.hero.title}
                    </h1>

                    <p className="text-2xl md:text-3xl text-gray-700 dark:text-gray-300 mb-8 max-w-3xl mx-auto font-light">
                        I am a <span className="font-semibold text-gray-900 dark:text-white">{content.hero.subtitle}</span>
                    </p>

                    <p className="text-lg md:text-xl text-gray-600 dark:text-gray-400 max-w-2xl mx-auto mb-10">
                        {content.hero.description}
                    </p>

                    <div className="flex justify-center gap-4">
                        <Link href={content.hero.cta.primary.href}>
                            <Button size="lg" className="cursor-pointer bg-gray-900 hover:bg-gray-800 text-white font-semibold px-8 py-4">
                                {content.hero.cta.primary.text}
                            </Button>
                        </Link>
                        <Link href={content.hero.cta.secondary.href}>
                            <Button size="lg" variant="outline" className="cursor-pointer font-semibold px-8 py-4 border-gray-300">
                                {content.hero.cta.secondary.text}
                            </Button>
                        </Link>
                    </div>
                </div>
            </section>
        </div>
    )
}

'use client'
import { useState } from 'react'
import Link from 'next/link'
import { usePathname } from 'next/navigation'
import { Menu, X } from 'lucide-react'
import { navigation, siteConfig } from '@/lib/constants/config'

export default function Navigation() {
  const [isMenuOpen, setIsMenuOpen] = useState(false)
  const pathname = usePathname()

  const navigationItems = navigation.map(item => ({
    name: item.title,
    path: item.href
  }))

  return (
    <nav className="fixed top-6 left-1/2 transform -translate-x-1/2 z-50">
      <div className="w-[90vw] md:w-fit bg-white/90 backdrop-blur-xl border border-gray-200 shadow-xl rounded-2xl px-8 py-4">
        {/* Desktop Navigation */}
        <div className="hidden md:flex items-center space-x-8">
          <Link key={"logo"} href="/" className="w-fit">
            <img src={siteConfig.logo} alt="Logo" className="w-8 h-8 rounded-full" />
          </Link>
          {navigationItems.map((item) => (
            <Link
              key={item.path}
              href={item.path}
              className={`text-sm font-medium transition-colors ${
                pathname === item.path 
                  ? `text-gray-900 after:content-[''] after:block after:ml-[20%] after:w-[80%] after:h-0.5 after:bg-gray-900 after:rounded-full` 
                  : 'text-gray-600 hover:text-gray-900'
              }`}
            >
              {item.name}
            </Link>
          ))}
        </div>

        {/* Mobile Navigation */}
        <div className="md:hidden flex items-center justify-between w-full">
          <Link href="/" className="font-bold text-xl text-gray-900">
            <img src={siteConfig.logo} alt="Logo" className="w-8 h-8 rounded-full" />
          </Link>
          <button
            onClick={() => setIsMenuOpen(!isMenuOpen)}
            className="p-2 text-gray-600 hover:text-gray-900"
          >
            {isMenuOpen ? <X size={20} /> : <Menu size={20} />}
          </button>
        </div>

        {/* Mobile Menu */}
        {isMenuOpen && (
          <div className="md:hidden absolute top-18 left-0 right-0 bg-white/95 backdrop-blur-xl border border-gray-200 rounded-xl p-4 shadow-xl">
            {navigationItems.map((item) => (
              <Link
                key={item.path}
                href={item.path}
                onClick={() => setIsMenuOpen(false)}
                className={`block py-3 px-4 text-sm font-medium rounded-lg transition-colors ${
                  pathname === item.path 
                    ? 'text-gray-900 bg-gray-100' 
                    : 'text-gray-600 hover:text-gray-900 hover:bg-gray-50'
                }`}
              >
                {item.name}
              </Link>
            ))}
          </div>
        )}
      </div>
    </nav>
  )
}
